export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "anne",
      },
      content: "첫번째 게시글 #해시태그",
      Images: [
        {
          src: "https://unsplash.com/ko/%EC%82%AC%EC%A7%84/%ED%9D%B0%EC%83%89%EA%B3%BC-%EA%B0%88%EC%83%89-%EB%A1%B1-%EC%BD%94%ED%8A%B8-%ED%81%B0-%EA%B0%9C-U3aF7hgUSrk",
        },
        {
          src: "https://unsplash.com/ko/%EC%82%AC%EC%A7%84/G9QPsPvw63g",
        },
        {
          src: "https://unsplash.com/ko/%EC%82%AC%EC%A7%84/Cubnd4g0F3c",
        },
      ],
      Comments: [
        {
          User: {
            nickname: "jenna",
          },
          content: "happy gummy bear",
        },
        {
          User: {
            nickname: "hero",
          },
          content: "jelly belly",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
};
const dummyPost = {
  id: 2,
  content: "dummy data",
  User: {
    id: 1,
    nickname: "jenna",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};
export default reducer;
